Trigger.script:
local priority = tonumber(matches[2]) local affs = string.split(matches[3],", ") local naughtyCharacters = {"%(","%)"}
--strip parentheses for _, aff in pairs(affs) do for _, char in pairs(naughtyCharacters) do aff = aff:gsub(char,"") end
ori.ssc.prios[aff] = priority end

Script.script:
ori.settings = ori.settings or { sep = "|", mountList = {}, priorityGags = false, deathsightEat = false, thirdeyeEat = false, hiddenChecks = false, autoReject = false, autoMount = false, pagelength = 30, lustList = {}, customPrompt = true, autoGallop = false, }
local descs = { sep = "Command Separator", mountList = "Mount IDs", priorityGags = "Curing priority gags", deathsightEat = "Eat for deathsight", thirdeyeEat = "Eat for thirdeye", pagelength = "Your desired pagelength", lustList = "Lust Whitelist", hiddenChecks = "Hidden affliction checks", autoReject = "Automatically reject", autoMount = "Automatically mount", autoGallop = "Toggle gallop on mounting", customPrompt = "Display a custom Prompt", }
local path = ori.filepath.."/orionSettings"
function ori.saveSettings() table.save(path, ori.settings) ori.echo("Settings saved") end
function ori.loadSettings() if not io.exists(path) then return end local tempSettings = {} table.load(path, tempSettings) for key, value in pairs(tempSettings) do ori.settings[key] = value end ori.echo("Settings loaded") end
function ori.changeSetting(setting, value) if descs[setting] then ori.settings[setting] = value ori.echo(descs[setting].." set to: "..tostring(value)) ori.saveSettings() else ori.echo("Invalid setting: "..setting) end end
local commands = { sep = 'clearCmdLine() appendCmdLine("ori sep ")', mountList = 'clearCmdLine() appendCmdLine("ori mountlist ")', priorityGags = 'ori.changeSetting("priorityGags", not ori.settings["priorityGags"])', deathsightEat = 'ori.changeSetting("deathsightEat", not ori.settings["deathsightEat"])', thirdeyeEat = 'ori.changeSetting("thirdeyeEat", not ori.settings["thirdeyeEat"])', hiddenChecks = 'ori.changeSetting("hiddenChecks", not ori.settings["hiddenChecks"])', autoReject = 'ori.changeSetting("autoReject", not ori.settings["autoReject"])', pagelength = 'clearCmdLine() appendCmdLine("config pagelength ")', lustList = 'clearCmdLine() appendCmdLine("ori lustlist ")', autoMount = 'ori.changeSetting("autoMount", not ori.settings["autoMount"])', autoGallop = 'ori.changeSetting("autoGallop", not ori.settings["autoGallop"])', customPrompt = 'ori.changeSetting("customPrompt", not ori.settings["customPrompt"])', }
function ori.showSettings() ori.echo("These are your current settings\n") for setting, desc in pairs(descs) do if type(ori.settings[setting]) == "table" then ori.echo(desc..": "..table.concat(ori.settings[setting], ", ")) echo(" ") cechoLink("<ansiGreen>[Add]", commands[setting]..(table.contains({"lustList", "mountList"}, setting) and [[ appendCmdLine("add " )]] or ""), "Click to add", true) echo(" ") cechoLink("<firebrick>[Remove]\n", commands[setting]..(table.contains({"lustList", "mountList"}, setting) and [[ appendCmdLine("remove " )]] or ""), "Click to remove", true) else cechoLink("<dark_orchid>[<light_slate_blue>Orion<dark_orchid>]<lavender>: <plum>"..desc..": "..tostring(ori.settings[setting]).."\n", commands[setting], "Click to change", true) end end -- echo("\n") moveCursorEnd() ori.prompt.custom() end

