ori.vitals = ori.vitals or {} ori.oldvitals = ori.oldvitals or {}
function ori.vitalsHandler() ori.balance.gmcp() ori.oldvitals = ori.vitals ori.vitals = {} for _, str in pairs(gmcp.Char.Vitals.charstats) do local key, value = unpack(string.split(str,": ")) value = tonumber(value) or value ori.vitals[key:lower()] = value end for _, key in pairs({"ep","wp","hp","mp","maxep","maxwp","maxhp","maxmp","nl"}) do if ori.affs.blackout then ori.vitals[key] = ori.oldvitals[key] else ori.vitals[key] = tonumber(gmcp.Char.Vitals[key]) end end ori.vitals.hpperc = 100*(ori.vitals.hp/ori.vitals.maxhp) ori.vitals.mpperc = 100*(ori.vitals.mp/ori.vitals.maxmp) end
registerAnonymousEventHandler("gmcp.Char.Vitals", "ori.vitalsHandler")

