function ori.defense.display() ori.echo("Current Defenses:") local class = ori.class()
local others = {}
for def, _ in pairs(ori.defs) do if not table.contains(ori.defense.groups.general, def) and not table.contains(ori.defense.groups[class], def) then table.insert(others, def) end end local groups = {ori.defense.groups.general, ori.defense.groups[class]}
if #others>0 then table.insert(groups, others) end local groupnames = {"General", class, "Other"}
for i, group in ipairs(groups) do local groupname = groupnames[i] cecho( "\n<RoyalBlue>(<MediumSlateBlue>====<RoyalBlue><<MediumSeaGreen>" .. groupname:title() .. "<RoyalBlue>><MediumSlateBlue>" .. string.rep("=", 89 - #groupname) .. "<RoyalBlue>)" ) local counter = 0 for _, defense in pairs(group) do if counter == 0 then cecho("\n" .. "<RoyalBlue>(~~]<MediumSlateBlue>: ") end counter = (counter + 1) % 6 --display cecho cecholink - toggles, and redisplays defences.edit cecho((ori.defs[defense] and "<PeachPuff>" or "<IndianRed>") .. defense) echo(string.rep(" ", 15 - #defense)) end end
cecho("\n<RoyalBlue>(<MediumSlateBlue>" .. string.rep("=", 95) .. "<RoyalBlue>)") echo("\n") moveCursorEnd() ori.prompt.custom() end

local order = {"ashtan", "cyrene", "eleusis", "hashan", "mhaldor", "targossas", "underworld", "none"}
function ori.ndb.cformat(player, format) local str = "<reset>" local color = ori.ndb.getColor(player) if color then str = str.."<"..color..">" end str = str..(ori.ndb.getBold(player) and "<b>" or "")..(ori.ndb.getItalic(player) and "<i>" or "")..(ori.ndb.getUnderline(player) and "<u>" or "") str = str..player:lower():title().."<reset>"..(format and format or "") return str end
function ori.ndb.qwsorted(people) local wrap = getWindowWrap() local sorted = {ashtan = {}, cyrene = {}, eleusis = {}, hashan = {}, mhaldor = {}, targossas = {}, underworld = {}, none = {}} for _, person in pairs(people.characters) do local name = person.name local org = ori.ndb.getCity(name) if org == "hidden" or org == "unknown" or org == "" then org = "none" end table.insert(sorted[org], name) end for _, org in ipairs(order) do local peoples = sorted[org] table.sort(peoples) if #peoples>0 then ori.newLine() orgName = (org~="none" and org or "rogue") cecho((ori.ndb.settings[org].color and "<"..ori.ndb.settings[org].color..">" or "").. string.rep(" ", 10-#orgName).."<b><u>"..orgName:title().."<reset>"..(#peoples>9 and "" or " ").."<white>(<cyan>"..#peoples.."<white>): ") for i, v in pairs(peoples) do local linelength = #getCurrentLine() local comma = next(peoples, i) local personlength = (comma and 2 or 0) + #v if linelength+personlength>wrap then echo("\n"..string.rep(" ",16)) moveCursorEnd() end cecho(ori.ndb.cformat(v)) if comma then echo(", ") end end end end echo("\n") moveCursorEnd() ori.prompt.custom() end

ori.prompt = ori.prompt or {}
function ori.prompt.getColor(percent) if percent>75 then return "<ansi_028>" elseif percent>25 then return "<yellow>" end return "<red>" end
function ori.prompt.printVital(current, max, letter, color) cecho(color..letter..":") if ori.affs.blackout then cecho("<orange>BLACKOUT ") else current, max = tonumber(current), tonumber(max) local percent = math.floor(current/max*100) local vColor = ori.prompt.getColor(percent) cecho(vColor..current..color.."|"..vColor..percent.."% ") end end
ori.prompt.functions = { health = function() ori.prompt.printVital(ori.vitals.hp, ori.vitals.maxhp, "H", "<lawn_green>") end, mana = function() ori.prompt.printVital(ori.vitals.mp, ori.vitals.maxmp, "M", "<dodger_blue>") end, endurance = function() ori.prompt.printVital(ori.vitals.ep, ori.vitals.maxep, "E", "<light_goldenrod>") end, willpower = function() ori.prompt.printVital(ori.vitals.wp, ori.vitals.maxwp, "W", "<medium_orchid>") end, rage = function() if ori.vitals.rage>0 then cecho("<sienna>Rage:<burlywood>"..ori.vitals. rage.." ") end end, target = function() end,
servertime = function() if not ori.serverTime then return end cecho("<dim_grey>"..ori.serverTime.." ") end,
targethealth = function() if gmcp.IRE.Target and gmcp.IRE.Target.Info then if ori.roomItems[gmcp.IRE.Target.Info.id] then local targetHealth = gmcp.IRE.Target.Info.hpperc:gsub("%%","") cecho("<sienna>T:<burlywood>"..gmcp.IRE.Target.Info.hpperc.." ") end end end, hchange = function() local change = ori.vitals.hp - ori.oldvitals.hp if math.abs(change)>99 then if change>0 then cecho("<spring_green>+"..change.."h ") else cecho("<tomato>"..change.."h ") end end end, mchange = function() local change = ori.vitals.mp - ori.oldvitals.mp if math.abs(change) > 99 then if change > 0 then cecho("<dodger_blue>+"..change.."m ") else cecho("<powder_blue>"..change.."m ") end end end, bleed = function() if ori.vitals.bleed>0 then cecho("<sienna>Bl:<firebrick>"..ori.vitals.bleed.." ") end end, locked = function() local locked = ori.locked() if locked then cecho("<red>[<yellow>"..locked.."<red>]") end end, }
ori.prompt.definition = {"servertime", "health","mana","endurance","willpower","locked","bleed","targethealth","rage", "hchange", "mchange"}
function ori.prompt.custom() if not ori.settings.customPrompt then return end replaceLine("") for _, thing in ipairs(ori.prompt.definition) do ori.prompt.functions[thing]() end end

ori.tattoos = ori.tattoos or {}

function ori.checkTattoos()
  enableTrigger("Gag tattoos")
  ori.quietSend("tattoos")
end

ori.cla = ori.cla or {}


function ori.cla.entSummoned(class, ent)
  ori.cla[class].ents[ent] = gmcp.Char.Items.Add.item.id
end

ori.cla.magi = ori.cla.magi or {}
ori.cla.magi.bals = ori.cla.magi.bals or {elementcall = true, bloodboil = true}
ori.cla.magi.ents = ori.cla.magi.ents or {}

function ori.cla.magi.reset()
  ori.cla.magi.bals = {elementcall = true, bloodboil = true}
  ori.cla.magi.ents = {}
end

registerAnonymousEventHandler("ori reset", "ori.cla.magi.reset")

ori.cla.fire = ori.cla.fire or {} ori.cla.fire.spark = ori.cla.spark or 0 ori.cla.fire.bals = ori.cla.fire.bals or {slough = true}
function ori.cla.fire.ignoreBurning() local affs = {"burning1", "burning2", "burning3", "burning4", "burning5"} if ori.class() == "fire elemental" then for _, aff in pairs(affs) do ori.ssc.changePriority(aff, 26) end else for _, aff in pairs(affs) do if ori.ssc.prios[aff] == 26 then ori.ssc.changePriority(aff, ori.ssc.defaultPrios[aff]) end end end end
registerAnonymousEventHandler("ori priority request","ori.cla.fire.ignoreBurning")

ori.cla.alch = ori.cla.alch or {}
ori.cla.alch.bals = ori.cla.alch.bals or {salt = true}
ori.cla.alch.ents = ori.cla.alch.ents or {}

function ori.cla.alch.reset()
  ori.cla.magi.bals = {elementcall = true, bloodboil = true}
  ori.cla.magi.ents = {}
end

registerAnonymousEventHandler("ori reset", "ori.cla.alch.reset")
